import styled from "styled-components";

export const ProductContainer = styled.div`
  position: relative;
  padding: 10px;
  margin: 5px;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 333px;
  background-color: white;
  transition: 0.2s;
`;

export const FreeShipping = styled.div`
  display: ${props => (props.product.isFreeShipping ? "block" : "none")};
  position: absolute;
  top: 10px;
  right: 10px;
  color: white;
  padding: 2px;
  background-color: black;
`;
