import React, { Component } from "react";

class ProductsContainer extends Component {
  render() {
    return (
      <div className="product-container">
        <div className="free-shipping">Free shipping</div>
        <div className="product-image-container">
          <img src={this.props.filteredProducts[0].image} />
        </div>
        <div className="product-info">
          <div className="product-title">
            {this.props.filteredProducts[0].title}
          </div>
          <div className="line" />
          <div className="product-price">
            $ {this.props.filteredProducts[0].price}
          </div>
          <div className="product-installments">
            or {this.props.filteredProducts[0].installments}x $
            {(
              this.props.filteredProducts[0].price /
              this.props.filteredProducts[0].installments
            ).toFixed(2)}
          </div>
          <button className="product-cart-btn">Add to cart</button>
        </div>
      </div>
    );
  }
}

export default ProductsContainer;
