import React, { Component } from "react";
import { observer } from "mobx-react";

import {
  StyledCartItem,
  CartItemImage,
  CartItemInfoWrapper,
  CrossMark,
  CartItemInfo,
  ItemName
} from "./styledComponents";
@observer
class CartItem extends Component {
  handleRemove = () => {
    this.props.store.removeFromCart(this.props.cartItem);
  };

  render() {
    return (
      <StyledCartItem>
        <div>
          <CartItemImage alt="cartitem" src={this.props.product.image} />
        </div>
        <CartItemInfoWrapper>
          <CrossMark onClick={this.handleRemove}>x</CrossMark>
          <CartItemInfo>
            <div>
              <ItemName>{this.props.product.title}</ItemName>
              <div className="cartItem.-style">
                {this.props.cartItem.size} | {this.props.product.style}
              </div>
              <div className="item-qty">
                cartItem. : {this.props.cartItem.quantity}
              </div>
            </div>
            <div className="item-price">
              <span>$</span>
              {this.props.product.price}
            </div>
          </CartItemInfo>
        </CartItemInfoWrapper>
      </StyledCartItem>
    );
  }
}

export default CartItem;
