import React, { Component } from "react";
import { observer } from "mobx-react";
import { action, computed, observable } from "mobx";
import CartItem from "../CartContainer/CartItem";

import {
  StyledCartContainer,
  CartToggle,
  CartCount,
  CartItemsContainer,
  InsideCartCount,
  CartItemsInnerWrap,
  CartIconInCart
} from "./styledComponents";

@observer
class CartContainer extends Component {
  @observable isClicked = false;
  @action cartClick = () => {
    this.isClicked = !this.isClicked;
  };

  @computed get cartIcon() {
    let cart = (
      <CartIcon alt="carticon" className="cart-image" src="assets/cart.png" />
    );
    return this.isClicked ? "x" : cart;
  }

  render() {
    return (
      <StyledCartContainer isClicked={this.isClicked}>
        <CartToggle onClick={this.cartClick}>
          {this.cartIcon}
          <CartCount show={!this.isClicked}>
            {this.props.store.cartItemsCount}
          </CartCount>
        </CartToggle>
        <CartItemsContainer>
          <CartItemsInnerWrap>
            <CartIconInCart>
              <CartToggle>
                <img
                  alt="carticon"
                  className="cart-image in-cart-image"
                  src="assets/cart.png"
                />
                <InsideCartCount>
                  {this.props.store.cartItemsCount}
                </InsideCartCount>
              </CartToggle>
              <div className="cart-text">Cart</div>
            </CartIconInCart>
            <div className="cart-items-wrapper">
              {this.props.store.cart.map(item => {
                return (
                  <CartItem
                    key={item.id}
                    product={this.props.store.getProduct(item.itemId)}
                    store={this.props.store}
                    cartItem={item}
                  />
                );
              })}
            </div>
          </CartItemsInnerWrap>
          <div className="cart-subtotal">
            <div className="sub-total">
              <span className="sub-total-text">SUBTOTAL</span>
              <span className="sub-total-count">
                <span>$</span>
                {this.props.store.totalCartValue}
              </span>
            </div>
            <div className="checkout">CHECKOUT</div>
          </div>
        </CartItemsContainer>
      </StyledCartContainer>
    );
  }
}

export default CartContainer;
