import styled from "styled-components";

export const StyledCartContainer = styled.div`
  position: fixed;
  z-index: 999;
  display: flex;
  right: ${props => (props.isClicked ? "0" : "-40vw")};
  transition: 0.5s;
`;

export const CartToggle = styled.div`
  position: relative;
  height: 60px;
  width: 60px;
  background-color: rgb(8, 7, 7);
  cursor: pointer;
  color: white;
  font-size: 42px;
  display: flex;
  justify-content: center;
  align-items: center;
`;
